private void setUpMap(com.google.android.gms.maps.model.LatLng latLng, java.lang.String country, java.lang.String address) {
    mMap.setInfoWindowAdapter(new com.thracecodeinc.myapp.CustomInfoWindowForMarker(this, country, populationMap.get(country), filenameOld));
    mMap.addMarker(new com.google.android.gms.maps.model.MarkerOptions().position(latLng).title(country).snippet(("Population: " + (java.text.NumberFormat.getNumberInstance(java.util.Locale.US).format(populationMap.get(country))))).icon(com.google.android.gms.maps.model.BitmapDescriptorFactory.defaultMarker(BitmapDescriptorFactory.HUE_AZURE))).showInfoWindow();
}