@org.junit.Test
public void should_find_all_mock_definitions_registered_in_class() {
    final com.pchudzik.springmock.infrastructure.definition.registry.DoubleRegistry registry = com.pchudzik.springmock.infrastructure.definition.registry.DoubleRegistryTestParser.parseClass(com.pchudzik.springmock.infrastructure.definition.registry.DoubleRegistryParserTest.MultipleAutowiredMocks.class);
    org.junit.Assert.assertEquals(registry.getMocks().size(), 2);
    org.junit.Assert.assertThat(registry.getMocks(), containsInAnyOrder(allOf(doubleWithName(com.pchudzik.springmock.infrastructure.definition.registry.DoubleRegistryParserTest.ANY_SERVICE1_NAME), doubleForClass(com.pchudzik.springmock.infrastructure.definition.registry.DoubleRegistryParserTest.AnyService1.class)), allOf(doubleWithName(com.pchudzik.springmock.infrastructure.definition.registry.DoubleRegistryParserTest.ANY_SERVICE2_NAME), doubleForClass(com.pchudzik.springmock.infrastructure.definition.registry.DoubleRegistryParserTest.AnyService2.class))));
}