private java.util.Set<de.sven_torben.cqrest.CommandInfo> createCommandInfos(final java.util.Collection<? extends java.lang.reflect.Method> cmdHandlers) {
    return cmdHandlers.stream().flatMap(( handler) -> java.util.Arrays.asList(handler.getParameterTypes()).stream()).filter(( paramType) -> this.commandType.isAssignableFrom(paramType)).map(( commandType) -> new de.sven_torben.cqrest.CommandInfo(commandType)).collect(java.util.stream.Collectors.toSet());
}