private void createDescriptionsForEndpointSwitches(com.orientechnologies.orient.core.db.record.ODatabaseRecordTx db) {
    com.baasbox.service.logging.BaasBoxLogger.info("Creating descriptions for endpoint switches...");
    com.baasbox.db.DbHelper.execMultiLineCommands(db, true, false, "update _BB_permissions set description='Access to APIs for reading and for asset downloading.' where tag='baasbox.assets';", "update _BB_permissions set description='Allows users to access their accounts, login and logout, modify their passwords.' where tag='baasbox.account';", "update _BB_permissions set description='Allows users to create new accounts (signup via username/password).' where tag='baasbox.account.create';", "update _BB_permissions set description='Allows login/signup through supported social networks.' where tag='baasbox.social';", "update _BB_permissions set description='Enables the workflow to reset a password via email.' where tag='baasbox.account.lost_password';", "update _BB_permissions set description=\'Allows to query users\\\' profiles.\' where tag=\'baasbox.users\';", "update _BB_permissions set description='Allows to know your followers or the people you follow on the social platform within BaasBox.' where tag='baasbox.friendship';", "update _BB_permissions set description='Enables the social functions of BaasBox: following/unfollowing among the users of the BaasBox server.' where tag='baasbox.friendship.create';", "update _BB_permissions set description='Allows registered users to send push notifications to other users. Administrators can always send notifications to users.' where tag='baasbox.notifications.send';", "update _BB_permissions set description='Allows apps to send device tokens they need in order to receive push notifications through BaasBox. If you disable these functions, apps wil no longer be able to register in order to receive push notifications from such server. Apps that are already registered will keep on receiving notifications.' where tag='baasbox.notifications.receive';", "update _BB_permissions set description='Allows to create new Documents.' where tag='baasbox.data.write';", "update _BB_permissions set description='Allows to read Collections and Documents by querying them.' where tag='baasbox.data.read';", "update _BB_permissions set description='Allows to update the contents of already existing Documents.' where tag='baasbox.data.update';", "update _BB_permissions set description='Enables the possibility to modify the ACL of Documents.' where tag='baasbox.data.grants';", "update _BB_permissions set description='Allows to download the Files stored in BaasBox and query them.' where tag='baasbox.file.read';", "update _BB_permissions set description=' Allows to create new Files.' where tag='baasbox.file.write';", "update _BB_permissions set description='Enables the possibility to modify the ACL of Files.' where tag='baasbox.file.grants';", "update _BB_permissions set description='Allows to make calls to plugins.' where tag='baasbox.scripts.invoke';");
    com.baasbox.service.logging.BaasBoxLogger.info("...done");
}