public static void drawRectangle(net.minecraft.util.ResourceLocation texture, java.awt.geom.Rectangle2D.Float rect, java.awt.geom.Rectangle2D.Float textureCoords, com.continuum.nova.gui.color vertexColor) {
    com.continuum.nova.gui.NovaDraw.drawRectangle(texture, rect, 0.5F, textureCoords, vertexColor);
}