public static org.springframework.xd.dirt.core.ModuleDescriptor createJobModuleDescriptor(java.lang.String jobName) {
    return new org.springframework.xd.dirt.core.ModuleDescriptor(new org.springframework.xd.module.ModuleDefinition(jobName, org.springframework.xd.module.ModuleType.job), jobName, jobName, 0, null);
}